const CalendarView = ({ selectionStart, selectionEnd, onDateClick, handleOpenNewBookingModal }) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const { bookings } = useBookings();
  const { currentUser, loading: authLoading } = useAuth(); 

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const daysInMonthCount = getDaysInMonth(year, month);
  let firstDayPos = getFirstDayOfMonth(year, month);
  firstDayPos = firstDayPos === 0 ? 6 : firstDayPos -1; // Mon=0 ... Sun=6

  const monthDays = Array.from({ length: daysInMonthCount }, (_, i) => i + 1);
  const leadingEmptyDays = Array.from({ length: firstDayPos });

  const handlePrevMonth = () => { setCurrentDate(new Date(year, month - 1, 1)); };
  const handleNextMonth = () => { setCurrentDate(new Date(year, month + 1, 1)); };

  const getBookingsForDate = (dateStr) => bookings.filter(b => {
    const current = new Date(dateStr + "T00:00:00"); // Ensure local date interpretation
    return current >= new Date(b.startDate  + "T00:00:00") && current <= new Date(b.endDate  + "T00:00:00");
  });

  if (authLoading) return <LoadingSpinner />; 
  if (!currentUser) return null; 

  return (
    <div className="container mx-auto p-4">
      <div className="bg-white p-6 rounded-lg shadow-xl">
        <div className="flex justify-between items-center mb-6">
          <button onClick={handlePrevMonth} className="p-2 rounded-full hover:bg-gray-200"><ChevronLeft size={28} className="text-blue-500" /></button>
          <h2 className="text-2xl font-bold text-blue-600">{currentDate.toLocaleString('de-DE', { month: 'long', year: 'numeric' })}</h2>
          <button onClick={handleNextMonth} className="p-2 rounded-full hover:bg-gray-200"><ChevronRight size={28} className="text-blue-500" /></button>
        </div>
        <div className="grid grid-cols-7 gap-1 text-center font-semibold text-gray-600 mb-2">
          {['Mo', 'Di', 'Mi', 'Do', 'Fr', 'Sa', 'So'].map(day => (<div key={day} className="py-2 text-xs sm:text-base">{day}</div>))}
        </div>
        <div className="grid grid-cols-7 gap-1">
          {leadingEmptyDays.map((_, index) => (<div key={`empty-${index}`} className="calendar-day-cell"></div>))}
          {monthDays.map(day => {
            const dayDateStr = formatDate(new Date(year, month, day));
            const dayBookings = getBookingsForDate(dayDateStr);
            const isBooked = dayBookings.length > 0;

            let cellStyle = isBooked ? 'bg-rose-100' : 'bg-green-50';
            const dayDateObj = new Date(dayDateStr + "T00:00:00");
            const selectionStartDateObj = selectionStart ? new Date(selectionStart + "T00:00:00") : null;
            const selectionEndDateObj = selectionEnd ? new Date(selectionEnd + "T00:00:00") : null;

            if (selectionStartDateObj && dayDateStr === selectionStart) {
              cellStyle = 'bg-blue-300 ring-2 ring-blue-500';
            } else if (selectionEndDateObj && dayDateStr === selectionEnd) {
              cellStyle = 'bg-blue-300 ring-2 ring-blue-500';
            } else if (selectionStartDateObj && selectionEndDateObj && dayDateObj > selectionStartDateObj && dayDateObj < selectionEndDateObj) {
              cellStyle = 'bg-blue-100';
            } else if (selectionStartDateObj && !selectionEndDateObj && dayDateStr === selectionStart) {
               cellStyle = 'bg-blue-300 ring-2 ring-blue-500'; // Highlight only start if end not yet selected
            }

            return (
              <div
                key={day}
                className={`calendar-day-cell p-1 sm:p-2 flex flex-col cursor-pointer hover:bg-gray-100 transition-colors duration-150 ${cellStyle}`}
                onClick={() => onDateClick(dayDateStr)}
              >
                <span className={`font-medium text-sm sm:text-base ${isBooked ? 'text-rose-700' : 'text-green-700'}`}>{day}</span>
                {dayBookings.map(booking => (
                  <div key={booking.id} className={`booking-indicator`}>
                    {booking.userName.split(' ')[0]} ({booking.status === BOOKING_STATUS.CONFIRMED ? 'B' : 'R'})
                  </div>
                ))}
              </div>
            );
          })}
        </div>
        <div className="mt-6 flex justify-between items-center">
            <div>
                {selectionStart && !selectionEnd && <p className="text-sm text-blue-600">Startdatum: {selectionStart}. Enddatum auswählen.</p>}
                {selectionStart && selectionEnd && <p className="text-sm text-green-600">Zeitraum ausgewählt: {selectionStart} - {selectionEnd}</p>}
            </div>
            <button onClick={handleOpenNewBookingModal} className="btn-primary flex items-center space-x-2">
                <PlusCircle size={20} /><span>Buchung ohne Vorauswahl</span>
            </button>
        </div>
      </div>
    </div>
  );
};

const BookingModal = ({ isOpen, onClose, initialStartDate, initialEndDate, bookingToEdit }) => {
  const { currentUser } = useAuth();
  const { addBooking, updateBooking, bookings } = useBookings();
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [userIdForBooking, setUserIdForBooking] = useState('');
  const [status, setStatus] = useState(BOOKING_STATUS.RESERVED);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const availableUsers = MOCK_USERS.filter(u => u.role === USER_ROLES.USER);

  useEffect(() => {
    if (bookingToEdit) {
      setStartDate(bookingToEdit.startDate);
      setEndDate(bookingToEdit.endDate);
      setStatus(bookingToEdit.status);
      setUserIdForBooking(bookingToEdit.userId); 
    } else if (initialStartDate && initialEndDate) {
      setStartDate(initialStartDate);
      setEndDate(initialEndDate);
      setStatus(BOOKING_STATUS.RESERVED);
      if (currentUser?.role !== USER_ROLES.ADMIN && currentUser) {
        setUserIdForBooking(currentUser.id);
      } else { setUserIdForBooking(''); }
    } else { // New booking without pre-selected range
        setStartDate(''); setEndDate('');
        setStatus(BOOKING_STATUS.RESERVED);
        setUserIdForBooking(currentUser?.role !== USER_ROLES.ADMIN && currentUser ? currentUser.id : '');
    }
  }, [isOpen, initialStartDate, initialEndDate, bookingToEdit, currentUser]);

  const isDateRangeAvailable = (start, end, excludeBookingId = null) => {
    if (!start || !end) return true; // Allow if dates are not yet set
    const newStart = new Date(start + "T00:00:00"); const newEnd = new Date(end + "T00:00:00");
    for (const booking of bookings) {
      if (excludeBookingId && booking.id === excludeBookingId) continue;
      const existingStart = new Date(booking.startDate + "T00:00:00");
      const existingEnd = new Date(booking.endDate + "T00:00:00");
      if (newStart <= existingEnd && newEnd >= existingStart) return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); setError(''); setIsLoading(true);
    if (!startDate || !endDate) { setError('Bitte Start- und Enddatum auswählen.'); setIsLoading(false); return; }
    if (new Date(endDate) < new Date(startDate)) { setError('Das Enddatum darf nicht vor dem Startdatum liegen.'); setIsLoading(false); return; }
    if (!isDateRangeAvailable(startDate, endDate, bookingToEdit?.id)) { setError('Der ausgewählte Zeitraum ist bereits belegt.'); setIsLoading(false); return; }

    let finalUserId, finalUserName;
    if (currentUser?.role === USER_ROLES.ADMIN) {
      if (!userIdForBooking) { setError('Als Admin Benutzer auswählen.'); setIsLoading(false); return; }
      finalUserId = userIdForBooking;
      const selUser = MOCK_USERS.find(u => u.id === userIdForBooking); finalUserName = selUser ? selUser.name : 'Unbek. Benutzer';
    } else if (currentUser) { finalUserId = currentUser.id; finalUserName = currentUser.name; } 
    else { setError('Sie müssen angemeldet sein.'); setIsLoading(false); return; }

    const bookingData = { userId: finalUserId, userName: finalUserName, startDate, endDate, status };
    try {
      if (bookingToEdit) { await updateBooking(bookingToEdit.id, bookingData); } 
      else { await addBooking(bookingData); }
      onClose(); // This will also reset calendar selection in AppContent
    } catch (err) { setError(err.message || 'Fehler beim Speichern.'); } 
    finally { setIsLoading(false); }
  };

  if (!isOpen) return null;
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3 className="text-xl font-semibold mb-4 text-gray-700">{bookingToEdit ? 'Buchung bearbeiten' : 'Neue Buchung'}</h3>
        {error && <p className="error-message">{error}</p>}
        <form onSubmit={handleSubmit} className="space-y-4">
          {currentUser?.role === USER_ROLES.ADMIN && (
            <div><label htmlFor="userSelect" className="form-label">Benutzer</label>
              <select id="userSelect" value={userIdForBooking} onChange={(e) => setUserIdForBooking(e.target.value)} className="input-style">
                <option value="">-- Benutzer wählen --</option>
                {availableUsers.map(user => ( <option key={user.id} value={user.id}>{user.name} ({user.email})</option> ))}
              </select>
            </div>
          )}
          <div><label htmlFor="startDate" className="form-label">Startdatum</label><input type="date" id="startDate" value={startDate} onChange={(e) => setStartDate(e.target.value)} required className="input-style" /></div>
          <div><label htmlFor="endDate" className="form-label">Enddatum</label><input type="date" id="endDate" value={endDate} onChange={(e) => setEndDate(e.target.value)} required className="input-style" /></div>
          <div><label htmlFor="status" className="form-label">Status</label>
            <select id="status" value={status} onChange={(e) => setStatus(e.target.value)} className="input-style">
              <option value={BOOKING_STATUS.RESERVED}>Reserviert</option><option value={BOOKING_STATUS.CONFIRMED}>Bestätigt</option>
            </select>
          </div>
          <div className="flex justify-end space-x-3 pt-2">
            <button type="button" onClick={onClose} className="btn-secondary">Abbrechen</button>
            <button type="submit" disabled={isLoading} className="btn-primary disabled:opacity-50">{isLoading ? 'Speichern...' : (bookingToEdit ? 'Speichern' : 'Erstellen')}</button>
          </div>
        </form>
      </div>
    </div>
  );
};

