    .filter(b => b.userName.toLowerCase().includes(searchTerm.toLowerCase()) || b.id.toLowerCase().includes(searchTerm.toLowerCase()))
    .filter(b => filterStatus ? b.status === filterStatus : true).sort((a,b) => new Date(a.startDate) - new Date(b.startDate));

  const handleDelete = async (bookingId) => { 
    console.warn(`TODO: Custom confirmation modal for deleting booking ${bookingId}`); if (true) await deleteBooking(bookingId); 
  }; 
