// Main App Component - Simplified to mainly provide context
export default function App() {
  return (<AuthProvider><BookingProviderWrapper><AppContent /></BookingProviderWrapper></AuthProvider>);
}

// AppContent now holds the main app logic and state
const AppContent = () => {
  const [currentView, setCurrentView] = useState('login');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [bookingModalParams, setBookingModalParams] = useState({ startDate: null, endDate: null, bookingToEdit: null });
  const [selectionStart, setSelectionStart] = useState(null); 
  const [selectionEnd, setSelectionEnd] = useState(null);   

  const { currentUser, loading: authLoading } = useAuth(); 

  useEffect(() => {
    if (authLoading) return; 
    const protectedViews = ['calendar', 'admin', 'userManagement']; const adminViews = ['admin', 'userManagement'];
    if (currentUser) {
      if (currentView === 'login') { setCurrentView('calendar'); } 
      else if (adminViews.includes(currentView) && currentUser.role !== USER_ROLES.ADMIN) { setCurrentView('calendar'); }
    } else { if (protectedViews.includes(currentView)) { setCurrentView('login'); } }
  }, [currentUser, currentView, authLoading, setCurrentView]);

  const handleCalendarDateClick = (dateStr) => {
    if (!selectionStart) {
      setSelectionStart(dateStr); setSelectionEnd(null);
    } else { 
      let finalStartDate = selectionStart; let finalEndDate = dateStr;
      if (new Date(dateStr + "T00:00:00") < new Date(selectionStart + "T00:00:00")) {
        finalStartDate = dateStr; finalEndDate = selectionStart;
      }
      setSelectionEnd(finalEndDate); // Update UI for highlighting
      setBookingModalParams({ startDate: finalStartDate, endDate: finalEndDate, bookingToEdit: null });
      setIsModalOpen(true);
    }
  };

  const handleOpenNewBookingModal = () => {
    setSelectionStart(null); setSelectionEnd(null);
    setBookingModalParams({ startDate: null, endDate: null, bookingToEdit: null });
    setIsModalOpen(true);
  };

  const handleOpenEditBookingModal = (booking) => {
    setSelectionStart(null); setSelectionEnd(null);
    setBookingModalParams({ startDate: booking.startDate, endDate: booking.endDate, bookingToEdit: booking });
    setIsModalOpen(true);
  };

  const closeBookingModal = () => {
    setIsModalOpen(false); setSelectionStart(null); setSelectionEnd(null);
  };

  const renderView = () => {
    if (authLoading && !currentUser) { return <LoadingSpinner />; }
    switch (currentView) {
      case 'login': return <LoginPage />; // setCurrentView removed as prop, Navbar uses one from AppContent
      case 'calendar': return <CalendarView selectionStart={selectionStart} selectionEnd={selectionEnd} onDateClick={handleCalendarDateClick} handleOpenNewBookingModal={handleOpenNewBookingModal} />;
      case 'admin': return <AdminDashboard handleOpenNewBookingModal={handleOpenNewBookingModal} handleOpenEditBookingModal={handleOpenEditBookingModal} />;
      case 'userManagement': return <UserManagementPage />;
      default: return <LoginPage />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 font-sans flex flex-col">
        <Navbar setCurrentView={setCurrentView} /> {/* setCurrentView for Navbar actions is from AppContent */}
        <main className="flex-grow">{renderView()}</main>
        <BookingModal isOpen={isModalOpen} onClose={closeBookingModal} 
            initialStartDate={bookingModalParams.startDate} 
            initialEndDate={bookingModalParams.endDate}
            bookingToEdit={bookingModalParams.bookingToEdit} />
        <Footer />
    </div>
  );
};

const BookingProviderWrapper = ({ children }) => {
  const auth = useAuth(); 
  if (auth === null) { return <LoadingSpinner />; }
  return <BookingProvider>{children}</BookingProvider>;
};

const Footer = () => (<footer className="bg-gray-800 text-white text-center p-4 mt-auto"><p>&copy; {new Date().getFullYear()} Ferienhaus Planer.</p><p className="text-xs mt-1">Demo-Anwendung.</p></footer>);

