    if (authLoading) return; 

    const protectedViews = ['calendar', 'admin', 'userManagement'];
    const adminViews = ['admin', 'userManagement'];

    if (currentUser) {
      if (currentView === 'login') {
        setCurrentView('calendar');
      } else if (adminViews.includes(currentView) && currentUser.role !== USER_ROLES.ADMIN) {
        setCurrentView('calendar'); 
      }
    } else {
      if (protectedViews.includes(currentView)) {
        setCurrentView('login'); 
      }
    }
  }, [currentUser, currentView, authLoading, setCurrentView]);

  const openBookingModal = (date, booking = null) => {
    setSelectedDateForModal(date);
    setBookingToEdit(booking);
    setIsModalOpen(true);
  };

  const closeBookingModal = () => {
    setIsModalOpen(false);
    setSelectedDateForModal(null);
    setBookingToEdit(null);
  };

  const renderView = () => {
    // This check is crucial: if auth is loading AND we don't have a user yet (e.g., initial app load), show spinner.
    // Once authLoading is false, the useEffect above will handle redirection if needed,
    // and then this renderView will render the correct component based on the updated currentView.
    if (authLoading && !currentUser) { 
        return <LoadingSpinner />;
    }

    // If not loading, or if loading but currentUser is already set (e.g. from a previous session if implemented),
    // proceed to render based on currentView. The useEffect handles redirection.
    switch (currentView) {
      case 'login':
        return <LoginPage setCurrentView={setCurrentView} />; // Pass setCurrentView for Navbar and direct actions
      case 'calendar':
        return <CalendarView openBookingModal={openBookingModal} />;
      case 'admin':
        return <AdminDashboard openBookingModal={openBookingModal} setBookingToEdit={setBookingToEdit} />;
      case 'userManagement':
        return <UserManagementPage />;
      default: 
        return <LoginPage setCurrentView={setCurrentView} />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 font-sans flex flex-col">
        <Navbar setCurrentView={setCurrentView} /> {/* setCurrentView is needed for Navbar actions */}
        <main className="flex-grow"> 
            {renderView()}
        </main>
        <BookingModal
            isOpen={isModalOpen}
            onClose={closeBookingModal}
            selectedDate={selectedDateForModal}
            bookingToEdit={bookingToEdit}
        />
        <Footer />
    </div>
  );
};

const BookingProviderWrapper = ({ children }) => {
  const auth = useAuth(); 
  // If auth context is not yet available (useAuth returns the initial null), show spinner.
  // This prevents children from trying to use a null context.
  if (auth === null) { 
    return <LoadingSpinner />; 
  }
  return <BookingProvider>{children}</BookingProvider>;
};

const Footer = () => {
    return (
        <footer className="bg-gray-800 text-white text-center p-4 mt-auto">
            <p>&copy; {new Date().getFullYear()} Ferienhaus Planer. Alle Rechte vorbehalten.</p>
            <p className="text-xs mt-1">Dies ist eine Demo-Anwendung.</p>
        </footer>
    );
}

