  if (!dateStr) return null;
  const parts = dateStr.split('-');
  return new Date(parseInt(parts[0], 10), parseInt(parts[1], 10) - 1, parseInt(parts[2], 10));
};

// Helper: Format Date object to YYYY-MM-DD string (local)
